function evaluateCandidate(candidate) {
  let score = 100;
  const flags = [];
  const normalize = str => str?.toLowerCase().trim();

  const resumeName = normalize(candidate.resume?.name);
  const linkedinName = normalize(candidate.linkedin?.full_name);
  if (resumeName && linkedinName && resumeName !== linkedinName) {
    flags.push("‚ö†Ô∏è Name mismatch");
    score -= 10;
  }

  if (candidate.github?.lastCommitDaysAgo > 90) {
    flags.push("‚ö†Ô∏è Inactive GitHub (no commits in 90+ days)");
    score -= 10;
  }

  const toxicCount = candidate.posts.filter(p => p.toxic).length;
  if (toxicCount > 0) {
    flags.push(`‚ö†Ô∏è Toxic content found in ${toxicCount} post(s)`);
    score -= toxicCount * 10;
  }

  const riskRating = score >= 90 ? "Low" : score >= 70 ? "Medium" : "High";
  const summary = flags.length > 0
    ? "Issues found: " + flags.join("; ")
    : "‚úÖ No significant discrepancies found. Recommend Low risk.";

  return {
    name: candidate.name,
    flags,
    score,
    riskRating,
    summary
  };
}

// Wrap each candidate's result in an object with `json` for n8n
