const items = $input.all().map((item) => item.json);
const today = new Date();

// Calculate Saturday and Sunday dates (i.e., last 2 days)
const saturday = new Date(today);
saturday.setDate(today.getDate() - 2);

const sunday = new Date(today);
sunday.setDate(today.getDate() - 1);

// Helper to match only MM-DD regardless of year
const formatMD = (date) => `${date.getMonth() + 1}-${date.getDate()}`;

const saturdayMD = formatMD(saturday);
const sundayMD = formatMD(sunday);

const results = [];

for (const item of items) {
  const birthday = new Date(item.Birthday);
  const anniversary = new Date(item["Work Aniverssery"]);

  if (formatMD(birthday) === saturdayMD || formatMD(birthday) === sundayMD) {
    item.isBirthday = true;
  }

  if (
    formatMD(anniversary) === saturdayMD ||
    formatMD(anniversary) === sundayMD
  ) {
    item.isAnniversary = true;
  }

  results.push(item);
}

return results;

