const items = $input.all();
const today = new Date();
const padZero = (n) => (n < 10 ? "0" + n : n.toString());

// Format today's MM-DD
const todayMD = `${padZero(today.getMonth() + 1)}-${padZero(today.getDate())}`;

const results = items.map((item) => {
  const birthday = new Date(item.json.birthday);
  const anniversary = new Date(item.json.work_anniversary);

  // Format birthday and anniversary as MM-DD
  const birthdayMD = `${padZero(birthday.getMonth() + 1)}-${padZero(birthday.getDate())}`;
  const anniversaryMD = `${padZero(anniversary.getMonth() + 1)}-${padZero(anniversary.getDate())}`;

  // Compare with today's MM-DD
  const isBirthday = birthdayMD === todayMD;
  const isAnniversary = anniversaryMD === todayMD;

  return { ...item.json, isBirthday, isAnniversary };
});

return results;

