const items = $("Generate birthdays and work Anniversaries messages").all();
const today = new Date();

const stats = {
  todayBirthdays: 0,
  todayAnniversaries: 0,
  totalCelebrations: 0
};

const todayCelebrations = [];

// Utility to check if a date is today (ignoring the year)
const isToday = (date) =>
  date.getDate() === today.getDate() && date.getMonth() === today.getMonth();

for (const item of items) {
  const { name, birthday, work_anniversary, email } = item.json;

  const birthdayDate = new Date(birthday);
  const anniversaryDate = new Date(work_anniversary);

  // Check if today is the birthday
  if (isToday(birthdayDate)) {
    stats.todayBirthdays++;
    stats.totalCelebrations++;
    todayCelebrations.push({
      id: Math.random().toString(36).substr(2, 9),
      employeeName: name,
      type: 'BIRTHDAY',
      date: birthdayDate.toISOString(),
      email
    });
  }

  // Check if today is the work anniversary
  if (isToday(anniversaryDate)) {
    stats.todayAnniversaries++;
    stats.totalCelebrations++;
    todayCelebrations.push({
      id: Math.random().toString(36).substr(2, 9),
      employeeName: name,
      type: 'ANNIVERSARY',
      date: anniversaryDate.toISOString(),
      yearsOfService: today.getFullYear() - anniversaryDate.getFullYear(),
      email
    });
  }
}

return [{ json: { stats, todayCelebrations } }];
