const rawText = $input.first().json.checklist;

// Step 1: Split lines
const lines = rawText.split('\n');

// Step 2: Parse each line
const tasks = lines.map(line => {
  const match = line.match(/ðŸ“Œ (.+?) - by (\d{4}-\d{2}-\d{2}) \((.+?)\)/);
  if (!match) return null;

  return {
    task: match[1],
    deadline: match[2],
    assignee: match[3]
  };
}).filter(Boolean); // Remove nulls from lines that didn't match

// Step 3: Return result (for n8n or elsewhere)
return tasks.map(task => ({ json: task }));

const rawText = $input.first().json.choices[0].message.content;

// Step 1: Extract JSON block using regex
const jsonMatch = rawText.match(/\{[\s\S]*\}/);

if (!jsonMatch) {
  throw new Error("No JSON object found in the response.");
}

let eventData;

try {
  // Step 2: Parse the matched JSON string
  eventData = JSON.parse(jsonMatch[0]);

  // Step 3: Prepare activities array
  const activitiesArray = eventData.activities
    ? eventData.activities.split(',').map(a => a.trim())
    : [];

  // Step 4: Return as n8n output
  return [
    {
      json: {
        theme: eventData.theme,
        venue: eventData.venue,
        activities: activitiesArray
      }
    }
  ];

} catch (error) {
  throw new Error("Failed to parse JSON: " + error.message);
}

// Extract input directly from JSON
const input = $input.first().json;

const event_date = input.event_date;
const team_size = parseInt(input.team_size, 10);
const budget_limit = parseInt(input.budget, 10);
const event_type = input.event_type;
const location = input.location;
const preferences = input.preferences;
const theme = input.theme;
const venue = input.venue;
const activities = input.activities;

// Validate and parse date
const [year, month, day] = event_date.split("-").map(Number);
const parsedDate = new Date(year, month - 1, day);
if (isNaN(parsedDate.getTime())) {
  throw new Error("Invalid event_date format. Use YYYY-MM-DD.");
}

// Generate checklist with dynamic deadlines
function subtractDays(date, days) {
  const copy = new Date(date);
  copy.setDate(copy.getDate() - days);
  return copy.toISOString().split("T")[0];
}

const checklistArray = [
  { task: "Book venue", deadline: subtractDays(parsedDate, 10), assignee: "HR Team" },
  { task: "Arrange transportation", deadline: subtractDays(parsedDate, 7), assignee: "Admin" },
  { task: "Order food (as per preferences)", deadline: subtractDays(parsedDate, 5), assignee: "HR Team" },
  { task: "Prepare team-building materials", deadline: subtractDays(parsedDate, 3), assignee: "Admin" },
  { task: "Send invites to team", deadline: subtractDays(parsedDate, 12), assignee: "HR Team" }
];

// Convert checklist array to single string
const checklist = checklistArray.map(item =>
  `ðŸ“Œ ${item.task} - by ${item.deadline} (${item.assignee})`
).join('\n');

// Estimate budget
const venueCost = team_size <= 20 ? 5000 : 8000;
const transportCost = team_size * 200;
const foodAndActivitiesCost = team_size * 350;
const subtotal = venueCost + transportCost + foodAndActivitiesCost;
const decorationsAndBuffer = Math.max(2000, Math.round(subtotal * 0.10));
const estimated_total = subtotal + decorationsAndBuffer;
const note = estimated_total > budget_limit
  ? `âš ï¸ Estimated budget â‚¹${estimated_total} exceeds provided budget of â‚¹${budget_limit}.`
  : "âœ… Estimated budget is within the provided limit.";

const budget = {
  venue_cost: venueCost,
  transport_cost: transportCost,
  food_and_activities: foodAndActivitiesCost,
  decorations_and_buffer: decorationsAndBuffer,
  estimated_total,
  budget_limit,
  note
};

// Final Output
return [{
  json: {
    event_type,
    event_date,
    location,
    preferences,
    attendees: team_size,
    theme,
    venue,
    checklist,  // now a string
    budget,
    activities
  }
}];

