    }
  };
});

const raw = $input.first().json.output;

// --- Extract Event Details ---
const dateMatch = raw.match(/ðŸ“… Date:\s*(\d{4}-\d{2}-\d{2})/);
const locationMatch = raw.match(/ðŸ“ ([^\n]+)/);
const attendeesMatch = raw.match(/ðŸ‘¥ Attendees:\s*(\d+)/);
const preferencesMatch = raw.match(/Suggested Plan:\s*Team Outing\s*â€“\s*(\[.*?\])/);

// --- Extract Suggestions ---
const themeMatch = raw.match(/ðŸ”¹ Theme:\s*(.+)/);
const venueMatch = raw.match(/ðŸ”¹ Venue:\s*(.+)/);

// --- Extract Activities (more flexible handling) ---
let activities = [];
const activitySectionMatch = raw.match(/ðŸ”¹ Activities:\s*\n([\s\S]+?)\n(?:ðŸ“|ðŸ’°|_Generated|$)/);

if (activitySectionMatch) {
  const activityBlock = activitySectionMatch[1].trim();

  try {
    // Try parsing as JSON array if possible
    if (activityBlock.startsWith("[") && activityBlock.endsWith("]")) {
      activities = JSON.parse(activityBlock.replace(/'/g, '"'));
    } else {
      // Otherwise treat as line-based list
      activities = activityBlock
        .split(/\n|,/)
        .map(line => line.replace(/^[-â€¢\s"]+|["\s]+$/g, '').trim())
        .filter(line => line.length > 0);
    }
  } catch (e) {
    activities = [];
  }
}

// --- Extract Checklist ---
const checklistRegex = /âœ… (.+?) - by (\d{4}-\d{2}-\d{2}) \((.+?)\)/g;
const checklist = [];
let match;
while ((match = checklistRegex.exec(raw)) !== null) {
  checklist.push({
    task: match[1].trim(),
    deadline: match[2],
    assignee: match[3]
  });
}

// --- Extract Budget ---
const budgetMatch = raw.match(/ðŸ’° Budget:\s*â‚¹(\d+)/);

// --- Get current timestamp ---
const timestamp = new Date().toISOString();

// --- Final output ---
return [{
  json: {
    timestamp,
    event_details: {
      event_type: "Team Outing",
      date: dateMatch?.[1] || 'Not found',
      location: locationMatch?.[1] || 'Not found',
      preferences: preferencesMatch ? JSON.parse(preferencesMatch[1].replace(/'/g, '"')) : [],
      attendees: parseInt(attendeesMatch?.[1] || 0)
    },
    suggestions: {
      theme: themeMatch?.[1].trim() || 'Not found',
      venue: venueMatch?.[1].trim() || 'Not found',
      activities
    },
    checklist,
    budget: {
      total: budgetMatch ? `â‚¹${budgetMatch[1]}` : 'Not found'
    },
    status: "Pending"
  }
}];
