let items = $input.all();
let names = items.map(e => e.json.Name);
let unique_names = [...new Set(names)];

let results = [];

for (let name of unique_names) {
  let matching_items = items.filter(e => e.json.Name === name);

  let totalDays = matching_items.reduce((sum, entry) => {
    return sum + parseInt(entry.json.Days.trim());
  }, 0);

  // Use first matching item as base for Type
  let type = matching_items[0].json.Type;

  results.push({
    json: {
      Name: name,
      Days: totalDays,
      Type: type
    }
  });
}

return results;

