const items = $input.all();
const stats = {
  casualLeaves: 0,
  sickLeaves: 0,
  totalEmployees: 50, // This should be fetched from your employee database
  activeRequests: 0
};

const recentActivity = [];

for (const item of items) {
  if (item.json.Type === 'Casual Leave') {
    stats.casualLeaves += parseInt($('Combine Holiday Counts').first().json.Days.toString());
  } else if (item.json.Type === 'Sick Leave') {
    stats.sickLeaves += parseInt($('Combine Illness Counts').first().json.Days.toString());
  }

  recentActivity.push({
    id: item.json.id || Math.random().toString(36).substr(2, 9),
    employeeName: item.json.Name,
    leaveType: item.json.Type.toUpperCase(),
    status: 'APPROVED',
    startDate: $('Classify Events').first().json.start.dateTime,
    endDate: $('Classify Events').first().json.end.dateTime,
    timestamp: new Date().toISOString()
  });
}

return [{
  json: {
    stats,
    recentActivity
  }
}];
