const allItems = $input.all().map(i => i.json.raw);

// --- STEP 1: Separate Leads and Historical Deals ---
const leads = [];
const historicalDeals = [];

for (const item of allItems) {
  if ('expected_close_date' in item && 'last_updated' in item) {
    leads.push(item);
  } else if ('cycle_length_days' in item) {
    historicalDeals.push(item);
  }
}

// --- STEP 2: Remove stale/inactive leads ---
const THIRTY_DAYS_AGO = new Date();
THIRTY_DAYS_AGO.setDate(THIRTY_DAYS_AGO.getDate() - 30);

const activeLeads = leads.filter(lead => {
  const isOpen = lead.status === 'Open';
  const lastUpdated = lead.last_updated ? new Date(lead.last_updated) : null;
  return isOpen && lastUpdated && lastUpdated >= THIRTY_DAYS_AGO;
});

// --- STEP 3: Trim top/bottom 1% outliers by deal value ---
const sortedValues = activeLeads.map(l => Number(l.value) || 0).sort((a, b) => a - b);
const lowIndex = Math.floor(sortedValues.length * 0.01);
const highIndex = Math.ceil(sortedValues.length * 0.99);
const minVal = sortedValues[lowIndex] || 0;
const maxVal = sortedValues[highIndex] || Number.MAX_SAFE_INTEGER;

const filteredLeads = activeLeads.filter(lead => {
  const val = Number(lead.value) || 0;
  return val >= minVal && val <= maxVal;
});

// --- STEP 4: Calculate average deal cycle length ---
const cycleDays = historicalDeals
  .map(deal => Number(deal.cycle_length_days))
  .filter(day => !isNaN(day) && day > 0);

const avgCycleLength = cycleDays.length > 0
  ? cycleDays.reduce((sum, val) => sum + val, 0) / cycleDays.length
  : 1;

// --- STEP 5: Define seasonality boost map ---
const seasonalityMap = {
  "APAC_CRM Tool": 1.12,
  "NA_Analytics": 0.95,
  "EMEA_Analytics": 1.05,
  "NA_HR System": 1.08,
  "APAC_Analytics": 1.10
};

// --- STEP 6a: Precompute adjusted values ---
const today = new Date();

const enrichedLeads = filteredLeads.map(lead => {
  const lastUpdated = new Date(lead.last_updated);
  const leadAgeDays = Math.ceil((today - lastUpdated) / (1000 * 60 * 60 * 24));
  const normalizedCycle = +(leadAgeDays / avgCycleLength).toFixed(2);

  const seasonKey = `${lead.region}_${lead.product}`;
  const seasonalityBoost = seasonalityMap[seasonKey] || 1;
  const adjustedValue = Math.round(lead.value * seasonalityBoost);

  return {
    lead,
    normalizedCycle,
    seasonalityBoost,
    adjustedValue
  };
});


// --- STEP 6c: Final Output with last_updated ---
const output = enrichedLeads.map(e => {
  const lead = e.lead;
  return {
    json: {
      id: lead.id,
      value: lead.value,
      rep: lead.rep,
      region: lead.region,
      product: lead.product,
      stage: lead.stage,
      status: lead.status,
      expected_close_date: lead.expected_close_date,
      last_updated: lead.last_updated,  // ‚úÖ Added this line
      normalized_lead_age: e.normalizedCycle,
      seasonality_multiplier: e.seasonalityBoost,
      adjusted_value: e.adjustedValue,
      category_key: `${lead.region}_${lead.product}_${lead.rep}_${lead.stage}`
    }
  };
});


return output;

// --- STEP 1: Get AI raw output ---
const raw = $input.all().map(i => i.json.output).join('\n');

// --- STEP 2: Extract valid JSON blocks only ---
const matches = raw.match(/{[\s\S]*?}/g); // find all JSON-like {...} blocks

if (!matches || matches.length === 0) {
  throw new Error("‚ùå No valid JSON blocks found in AI output.");
}

// --- STEP 3: Clean, fix, and join JSON blocks ---
function fixJsonKeys(jsonStr) {
  return jsonStr
    // Quote unquoted keys
    .replace(/([{,]\s*)([a-zA-Z0-9_]+)\s*:/g, '$1"$2":')

    // Quote unquoted string values
    .replace(/:\s*([a-zA-Z][a-zA-Z0-9 _-]*)(?=[,\}])/g, (match, p1) => {
      const trimmed = p1.trim();
      if (["true", "false", "null"].includes(trimmed) || /^[\d.-]+$/.test(trimmed)) {
        return `: ${trimmed}`;
      }
      return `: "${trimmed}"`;
    })

    // Remove trailing commas
    .replace(/,\s*([}\]])/g, '$1')

    // Remove newlines and trim
    .replace(/\n/g, '')
    .trim();
}

const cleaned = matches
  .map(block => {
    try {
      const fixed = fixJsonKeys(block);
      JSON.parse(fixed); // Validate block
      return fixed;
    } catch {
      return null; // Skip invalid blocks
    }
  })
  .filter(Boolean)
  .join(',');

const jsonArray = `[${cleaned}]`;

// --- STEP 4: Parse JSON array ---
let dealsData;
try {
  dealsData = JSON.parse(jsonArray);
} catch (e) {
  throw new Error("‚ùå Failed to parse AI output into valid JSON: " + e.message + "\nüîç Cleaned JSON: " + jsonArray);
}

// --- STEP 5: Config ---
const target = 110000;

// --- STEP 6: Forecast Calculations ---
function toNumber(val) {
  if (typeof val === 'number') return val;
  if (typeof val === 'string') return parseFloat(val.replace(/[^\d.-]/g, '')) || 0;
  return 0;
}

const totalWeightedValue = dealsData.reduce((sum, deal) => sum + toNumber(deal.weighted_pipeline_value), 0);
const totalThisWeek = dealsData.reduce((sum, deal) => sum + toNumber(deal.forecast_this_week), 0);
const totalThisMonth = dealsData.reduce((sum, deal) => sum + toNumber(deal.forecast_this_month), 0);
const totalThisQuarter = dealsData.reduce((sum, deal) => sum + toNumber(deal.forecast_this_quarter), 0);

const expectedMin = Math.round(totalWeightedValue * 0.9);
const expectedMax = Math.round(totalWeightedValue * 1.2);

const riskOfShortfall = totalWeightedValue < target
  ? Math.round((1 - totalWeightedValue / target) * 100)
  : 0;

// --- STEP 7: Top Deals ---
const topDeals = [...dealsData]
  .sort((a, b) => toNumber(b.forecast_confidence) - toNumber(a.forecast_confidence))
  .slice(0, 5)
  .map(deal => ({
    id: deal.id,
    value: toNumber(deal.value),
    stage: deal.stage,
    confidence: toNumber(deal.forecast_confidence),
    rep: deal.rep
  }));

// --- STEP 8: Visualizations ---
const stageFunnel = {};
for (const deal of dealsData) {
  if (deal.stage) {
    stageFunnel[deal.stage] = (stageFunnel[deal.stage] || 0) + 1;
  }
}

const forecastVsTarget = {
  forecast: Math.round(totalWeightedValue),
  target
};

const heatmap = {};
for (const deal of dealsData) {
  const region = deal.region || 'Unknown';
  const product = deal.product || 'Unknown';
  const key = `${region}__${product}`;
  heatmap[key] = (heatmap[key] || 0) + 1;
}

// --- STEP 9: Output ---
return [
  {
    json: {
      summary: {
        expected_revenue_range: `‚Çπ${expectedMin.toLocaleString()} ‚Äì ‚Çπ${expectedMax.toLocaleString()}`,
        forecast_this_week: totalThisWeek,
        forecast_this_month: totalThisMonth,
        forecast_this_quarter: totalThisQuarter,
        top_5_deals: topDeals,
        risk_of_shortfall: `${riskOfShortfall}%`,
        timestamp: new Date().toISOString()
      },
      visualizations: {
        forecast_vs_target: forecastVsTarget,
        stage_funnel: stageFunnel,
        region_product_heatmap: heatmap
      }
    }
  }
];

// Get input data
const input = $input.all()[0].json;

const expectedRevenueRange = input.summary.expected_revenue_range;
const riskOfShortfall = input.risk_of_shortfall;
const forecast = input.visualizations.forecast_vs_target.forecast;
const target = input.visualizations.forecast_vs_target.target;

