const forecastSummary = `
üì¨ *SalesForecast AI ‚Äì Weekly Forecast Summary*

üßÆ *Expected Revenue:* ${expectedRevenueRange}
üéØ *Target:* ‚Çπ${target.toLocaleString()}
üìâ *Risk of Shortfall:* ${riskOfShortfall}

üîù *High Confidence Deals (Closing This Week):*
${formattedDeals}

üìä *Funnel Conversion Rate:* ${funnelConversionRate}
_Generated by QAID's SalesForecast AI Agent_
`.trim();

